import FakeBaycABI from "../ABI/FakeBAYC.json"
import { useState, useEffect } from 'react'
import Web3 from "web3";
import { useParams } from 'react-router-dom';



function FakeBaycTokenInfo() {

    //state zone 
    const[tokenId, setTokenId] = useState(0);
    const [account, setAccount] = useState(null); 
    const [web3, setWeb3] = useState(null);
    const[attribute, setAttribute]= useState(); 
    const [image, setImage] = useState("");
    const [chain, setChain] = useState(null);
    const loading=false;

    //retrieve tokenID from url
    const currenttokenId  = useParams();
    useEffect(() => {
        checkNetwork();
        checkAccount();
        checkTokenId();
    }, [])

    async function checkNetwork(){
        let web3 = new Web3(window.ethereum)
        const chainID = await web3.eth.getChainId()
        await setChain(chainID);
    }
    async function checkAccount() {
        let web3 = new Web3(window.ethereum)
        setWeb3(web3)
        const accounts = await web3.eth.getAccounts()
        setAccount(accounts[0])
    }

    async function checkTokenId(){
        console.log(currenttokenId);
        await setTokenId(currenttokenId);
    }

    async function getFakeBAYCdata(){
        const fakeBaycData = FakeBaycABI.networks[chain];
        if(fakeBaycData){
            const contrat = new web3.eth.Contract(FakeBaycABI.abi, fakeBaycData.address)
            console.log("le {tokenId} est : " + tokenId.toString());
            let info = await contrat.methods.tokenURI(tokenId.toString()).call();
            const jsonURI = await fetch(info).then(res => res.json()); 
            setAttribute(JSON.stringify(jsonURI.attributes));  
            setImage(jsonURI.image); 
            console.log(jsonURI);

        } else{
            window.alert('Error : FakeBAYC contract not deployed / no detected network')
        }
        loading =true;
    }



    return (
        <div>
            <button onClick={getFakeBAYCdata}>Click here to get specific FakeBAYC infos</button>
            {loading && <>
                <p style={{color:"black"}}>Token ID : {tokenId}</p>
            </>
            }            
        </div>           
    )
}

export default FakeBaycTokenInfo;
