import React,{Component} from "react";
import FakeBaycABI from "../ABI/FakeBAYC.json"
import Web3 from "web3";


class FakeBayc extends Component {

    async UNSAFE_componentWillMount(){
        await this.loadWeb3()
        await this.loadBlockchainData()
    }

    async loadWeb3(){
        if(window.ethereum){
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        } 
        else if(window.web3){
            window.web3 = new Web3(window.web3.currentProvider)
        }
        else{
            window.alert('No etehereum browser detected ! Check out your Metamask!')
        }
    }

    async loadBlockchainData(){
        const web3 = window.web3
        const account = await web3.eth.getAccounts()
        this.setState({account : account[0]})
        const networkId = await web3.eth.net.getId()


        //Load Tether Contract
        const fakeBaycData = FakeBaycABI.networks[networkId]

        if(fakeBaycData){
            const FakeBaycABI = new web3.eth.Contract(FakeBaycABI.abi, fakeBaycData.address)
            this.setState({FakeBaycABI})
            let tetherBalance = await FakeBaycABI.methods.balanceOf(this.state.account).call()
            this.setState({tetherBalance : tetherBalance.toString()})
            console.log({balance: tetherBalance})
        } else{
            window.alert('Error : tether contract not deployed / no detected network')
        }

        this.setState({loading : false})

    }

    constructor(props){
        super(props)
        this.state = {
            account : '0x0',
            fakeBAYCbalance: '0',
            loading:true

        }
    }

  render() {
    return(
    <div>FakeBayc</div>
    )
  }
}

export default FakeBayc;
