}

 //render
    return (
        <div>
            <button onClick={info}>Click here to get FakeBAYC info</button>
            <div style={{color:"black"}}>FakeBayc number: {fakeBAYCbalance}</div>
            <button onClick={claim}>Claim New Token</button>
        </div>
    ); 
}


export default FakeBayc;
