import FakeBaycABI from "../ABI/FakeBAYC.json"
import { useState, useEffect } from 'react'
import Web3 from "web3";
import { matchPath } from "react-router-dom";

function FakeBaycTokenInfo() {

//State definition  
const [account, setAccount] = useState(null);
const [chain, setChain] = useState(null); 
const [web3, setWeb3] = useState(null);

console.log(params.tokenId);


useEffect(() => {
    activate();
    checkNetwork();
}, [])

// invoke to connect to wallet account
async function activate() {
  if (window.ethereum) {
    try {
      await window.ethereum.request({ method: 'eth_requestAccounts' });
      checkAccount()
    } catch (err) {
      console.log('user did not add account...', err)
    }
  }
}

// invoke to check if account is already connected
async function checkAccount() {
  let web3 = new Web3(window.ethereum)
  setWeb3(web3)
  const accounts = await web3.eth.getAccounts()
  setAccount(accounts[0])
}

async function checkNetwork(){
  let web3 = new Web3(window.ethereum)
  const chainID = await web3.eth.getChainId()
  if(chainID!="11155111"){
    await switchNetwork();
  }
  else{
    await setChain(chainID)
  }
}

async function switchNetwork() {
  let web3 = new Web3(window.ethereum)
  await window.ethereum.request({
  method: 'wallet_switchEthereumChain',
  params: [{ chainId: '0x5' }],    // Sepolia n'est pas natif pour la fonction de metamask
  });
  const chainID = await web3.eth.getChainId()
  setChain(chainID)
}



    return (
        <div>
            <h1>FakeBayc Token Info</h1>
        </div>                    
    )
}

export default FakeBaycTokenInfo;
