import { useState, useEffect } from "react";
import { IpfsImage } from 'react-ipfs-image';
import Web3 from "web3";
import FakeNefturianABI from "../ABI/FakeNefturian.json"
import { useParams } from 'react-router-dom';

function FakeNefturians(){

    const[price, setPrice]= useState();  

    useEffect(() => {
        GetTokenPrice();
    }, [])

    //contract zone 
    const contract_abi = FakeNefturianABI.abi; 
    const contract_address = "0x9bAADf70BD9369F54901CF3Ee1b3c63b60F4F0ED"; 
    let web3 = new Web3(window.ethereum);
    var contract = new web3.eth.Contract(contract_abi, contract_address);

    async function GetTokenPrice(){
        let price = await contract.methods.tokenPrice().call();
        console.log(web3.utils.fromWei(String(price+1), 'ether'));
        setPrice(String(price+0.001));
    }

    async function BuyToken(){
        const accounts = await window.ethereum.request({method: 'eth_requestAccounts' }); 
        await contract.methods.buyAToken().send({from: accounts[0], value: price}).then(console.log);
    }

    return (
        <div>
            <h2><b>FakeNefturian</b></h2>
            {price &&
                <>
                    <h3>FakeNefturian Price :</h3>
                    <div><b>{web3.utils.fromWei(price, 'ether')} ETH</b></div>
                    <br></br>
                    <br></br>
                </>
            }
            <button onClick={BuyToken}>Buy FakeNefturian Token at floor price</button>
        </div>                    
    )

}

export default FakeNefturians;
