import { useState, useEffect } from "react";
import { IpfsImage } from 'react-ipfs-image';
import Web3 from "web3";
import FakeNefturianABI from "../ABI/FakeNefturian.json"
import { useParams } from 'react-router-dom';

function FakeNefturians(){
    const[tokenId, setTokenId] = useState(); 
    const[attribute, setAttribute]= useState();
    const[price, setPrice]= useState();  
    const [image, setImage] = useState(""); 

    const currenttokenId  = useParams();
    useEffect(() => {
        setTokenId(currenttokenId.tokenId);
    }, [])

    //contract zone 
    const contract_abi = FakeNefturianABI.abi; 
    const contract_address = "0x9bAADf70BD9369F54901CF3Ee1b3c63b60F4F0ED"; 
    let web3 = new Web3(window.ethereum);
    var contract = new web3.eth.Contract(contract_abi, contract_address);

    const handleChamp = (event)=>{
        setTokenId(event.target.value)
    }

    async function GetTokenInfo(){
        let info= await contract.methods.tokenURI(tokenId).call();
        const jsonURI = await fetch(info).then(res => res.json()); 
        setAttribute(JSON.stringify(jsonURI.attributes));  
        setImage(jsonURI.image); 
        console.log(jsonURI); 
    }

    async function GetTokenPrice(){
        let price = await contract.methods.tokenPrice(tokenId).call();
        setPrice(price);
        console.log(price);
    }

    async function BuyToken(){
        const accounts = await window.ethereum.request({method: 'eth_requestAccounts' }); 
        await contract.methods.buyAToken().send({from: accounts[0], value: price}).then(console.log);
    }

    return (
        <div>
            <input type="text"value={tokenId} onChange={e=>handleChamp(e)}/>
                <br></br>
                    <button onClick={GetTokenInfo}> Get token info</button>
                    <br></br>
                <br></br>
            {image &&
                <>
                    <IpfsImage hash={image}/>
                    <h3>DATAS :</h3>
                    <div>{attribute}</div>
                    <br></br>
                    <br></br>
                </>
            }
        </div>                    
    )

}

export default FakeNefturians;
